let PlugInBlock = require('../PlugInBlock');
let {THREE, Nodes} = require('../../three');

/**
 * ColorMapping class
 *
 @extends PlugInBlock
 */
class ColorMapping extends PlugInBlock {

  /**
   * Constructor for color block
   *
   * @param {PlugInBlock} parentBlock - The block before this color one
   * factor adapted to data order of magnitude)
   */
  constructor (parentBlock, colorMap = 'viridis') {
    let setters = {
      'colorMap': (name) => {
        if (this._textureMaps.get(name)) {
          this._colorMappingCall.inputs.texColorMap.value = this._textureMaps.get(name);

          this.updateMaterial();
        } else { throw new Error(`Color map "${name}" does not exist`); }
      },
      'colorMapMin': (value) => {
        if (value <= this._colorMapMaxNode.value && value >= this._dataMin) {
          this._colorMapMinNode.value = value;
        } else {
          this._colorMapMinNode.value = this._dataMin;
        }
      },
      'colorMapMax': (value) => {
        if (value >= this._colorMapMinNode.value && value <= this._dataMax) {
          this._colorMapMaxNode.value = value;
        } else {
          this._colorMapMaxNode.value = this._dataMax;
        }
      }
    };

    super(parentBlock, setters);

    this._textureMaps = undefined;
    this._colorMapping = undefined;
    this._colorMappingCall = undefined;
    this._colorMap = colorMap;

    this.inputDataDim = 1;
  }

  _process () {
    this._textureMaps = this._loadTextureMaps();

    this._colorMapping = new Nodes.FunctionNode(
      `vec3 colorMappingFunc${this._plugInID}(sampler2D texColorMap, float colorMapMin, float colorMapMax, float data){
        return vec3(texture2D(
          texColorMap,
          vec2(( data - colorMapMin ) / ( colorMapMax - colorMapMin ),
          0.0)));
      }`
    );

    this._colorMappingCall = new Nodes.FunctionCallNode(this._colorMapping);

    this._dataMin = this.getComponentMin(this._inputDataName, this._inputComponentNames[0]);
    this._dataMax = this.getComponentMax(this._inputDataName, this._inputComponentNames[0]);
    this._colorMapMinNode = new Nodes.FloatNode(this._dataMin);
    this._colorMapMaxNode = new Nodes.FloatNode(this._dataMax);

    this._colorMappingCall.inputs.data = this._inputDataNode;
    this._colorMappingCall.inputs.colorMapMin = this._colorMapMinNode;
    this._colorMappingCall.inputs.colorMapMax = this._colorMapMaxNode;

    this._colorMappingCall.inputs.texColorMap = new Nodes.TextureNode(this._textureMaps.get(this._colorMap));

    this.addColorNode('REPLACE', this._colorMappingCall);
  }

  /**
   * Change input for color effect
   * @param {string} dataName - Name of the input data of which you want
   * to set input components for this plug-in
   * @param {string[]} componentNames - A list of components that you
   * want as input.
   */
  _setInput (dataName, componentNames) {
    super._setInput(dataName, componentNames);

    if (this._processed && this._colorMappingCall) {
      this._dataMin = this.getComponentMin(dataName, componentNames[0]);
      this._dataMax = this.getComponentMax(dataName, componentNames[0]);
      this._colorMapMinNode.value = this._dataMin;
      this._colorMapMaxNode.value = this._dataMax;
      this._colorMappingCall.inputs.data = this._inputDataNode;

      this.updateMaterial();
    }
  }

  _loadTextureMaps () {
    let textureMaps = new Map();
    let textureLoader = new THREE.TextureLoader();
    textureMaps.set('viridis', textureLoader.load('data:image/png;base64,'));
    textureMaps.set('plasma', textureLoader.load('data:image/png;base64,'));
    textureMaps.set('magma', textureLoader.load('data:image/png;base64,'));
    textureMaps.set('inferno', textureLoader.load('data:image/png;base64,'));

    return textureMaps;
  };
}

module.exports = ColorMapping;
